function upsampleData(data, outputFreq) {
    // Initialize variables
    let outputData = [];

    // Loop through the input data
    for (let i = 0; i < data.length - 1; i++) {
        // Convert the input time strings to Date objects
        const time1 = new Date(data[i].datetime);
        const time2 = new Date(data[i + 1].datetime);

        // Calculate the time difference between the two input data points
        const timeDiff = time2.getTime() - time1.getTime();

        // Calculate the number of output data points to be generated within this time interval
        const numOutputs = Math.floor(timeDiff / (outputFreq * 1000));

        // Calculate the temperature increment per output data point
        const tempIncrement = (data[i + 1].temperature - data[i].temperature) / numOutputs;
        const humIncrement = (data[i + 1].humidity - data[i].humidity) / numOutputs;

        // Loop through each output data point within this time interval and generate the temperature value using linear interpolation
        for (let j = 0; j < numOutputs; j++) {
            const timestamp = time1.getTime() + j * outputFreq * 1000;
            const temp = data[i].temperature + j * tempIncrement;
            const hum = data[i].humidity + j * humIncrement;
            outputData.push({ datetime: new Date(timestamp).toISOString(), temperature: temp, humidity: hum });
        }
    }

    // Add the last input data point to the output data
    outputData.push(data[data.length - 1]);

    return outputData;
}

return {
    payload: upsampleData(msg.payload, 5),
};

function downsampleData(data, outputFreq) {
    // Initialize variables
    let outputData = [];
    let tempSum = 0;
    let numTemps = 0;
    let lastOutputTime = null;

    // Loop through the input data
    for (let i = 0; i < data.length; i++) {
        // Convert the input time string to a Date object
        const time = new Date(data[i].time);

        console.log(data[i].time);

        // If this is the first data point, set the last output time to the input time
        if (lastOutputTime === null) {
            lastOutputTime = time;
        }

        // Add the temperature value to the sum and increment the count
        tempSum += data[i].temp;
        numTemps++;

        // If the time difference between the current input data point and the last output data point is greater than or equal to the desired output frequency, generate a new output data point
        const timeDiff = time.getTime() - lastOutputTime.getTime();
        if (timeDiff >= outputFreq * 1000) {
            const tempAvg = tempSum / numTemps;
            outputData.push({ time: lastOutputTime.toISOString(), temp: tempAvg });
            tempSum = 0;
            numTemps = 0;
            lastOutputTime = new Date(lastOutputTime.getTime() + outputFreq * 1000);
        }
    }

    // Add the last output data point (if any)
    if (numTemps > 0) {
        const tempAvg = tempSum / numTemps;
        outputData.push({ time: lastOutputTime.toISOString(), temp: tempAvg });
    }

    return outputData;
}

return {
